package com.shaba.dec10;

public class Array2D {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int x[][]= {
				
				{1,2,3,4},
				{0,3,6,9},
				{5,6,7,8},
		};
		
		for (int i=0;i<3;i++)
				
				
		{
	      for (int j=0;j<4;j++)
	      {
	    	  System.out.print(" "+x[i][j]);
	    	 
	      }
	      System.out.println();
	      
		}
	}
}

	


