package com.shaba.dec10;

//Do While 

public class DoWhileSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//executes the statment and then checks the condition
		
		
		int x =10;
		do {
			System.out.println("value of x= "+x);
			System.out.println();
			x++;
		 	
			
		}while (x<21);
		
		

	}

}
